import React, {useState} from 'react';
import styled from "styled-components";
import How from '../img/how1.jpg';
import MiniCard from "./MiniCard";
import Play from '../img/play2.png';

const Container = styled.div`
  display: flex;
`;

const Left = styled.div`
  width: 50%;
`;

const Image = styled.img`
  display: ${props => props.open && "none"};
  height: 100%;
  margin-left: 100px;
`;

const Right = styled.div`
  width: 50%;
`;

const Wrapper = styled.div`
  padding: 50px;
  display: flex;
  flex-direction: column;
`;

const Title = styled.h1``;

const Desc = styled.p`
  font-size: 20px;
  margin-top: 20px;
  color: #555;
`;

const CardContainer = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 50px;
`;

const Button = styled.button`
  width: 180px;
  border: none;
  border-radius: 10px;
  background-color: darkblue;
  color: white;
  font-size: 20px;
  padding: 15px;
  margin-top: 50px;
  cursor: pointer;
  display: flex;
  align-items: center;

`;

const Icon = styled.img`
  width: 20px;
  margin-right: 10px;

  background-color: white;
`;

const Service = () => {
    const [open, setOpen] = useState(false);
    return (
        <Container>
            <Left>
                <Image open={open} src={How}/>
            </Left>
            <Right>
                <Wrapper>
                    <Title>Simple process to start</Title>
                    <Desc>We provide Lorem ipsum dolor sit amet, consectetur adipisicing elit. Assumenda atque error
                        impedit molestiae natus non perferendis quas quidem repellendus sapiente.Assumenda atque error
                        impedit molestiae natus non perferendis quas quidem repellendus sapiente.
                    </Desc>
                    <CardContainer>
                        <MiniCard/>
                        <MiniCard/>
                        <MiniCard/>
                    </CardContainer>
                    <Button onClick={() => setOpen(true)} >
                        <Icon src={Play}/>How it works
                    </Button>
                </Wrapper>
            </Right>
        </Container>
    );
};

export default Service;
