import React from 'react';
import styled from "styled-components";
import Woman from '../img/woman.jpg';

const Container = styled.div`
  height: calc(100vh - 50px);
  display: flex;
  padding: 20px;
`;

const Left = styled.div`
  width: 60%;
  display: flex;
  flex-direction: column;
  justify-content: center;
`;
const Title = styled.h1`
  font-size: 60px;
  width: 60%;
`;

const Desc = styled.p`
  width: 60%;
  font-size: 20px;
  margin-top: 10px;
`;

const Info = styled.div`
  width: 60%;
  margin-top: 50px;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const Button = styled.button`
padding: 15px;
  background-color: darkblue;
  color: white;
  border-radius: 10px;
  font-weight: bold;
  border: none;
  letter-spacing: 2px;
  cursor: pointer;
  
`;

const Contact = styled.div`
  display: flex;
  flex-direction: column;
`;

const Phone = styled.span`
  color: #f0667d;
  font-weight: bold;
  
`;

const ContactText = styled.span`
  color: gray;
  margin-top: 5px;
`;
const Right = styled.div`
  width: 40%;
`;

const Image =styled.image`
width: 100px;
`;


const Intro = () => {
    return (
        <Container>
            <Left>
                <Title>Anventures in creative age</Title>
                <Desc>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Fugit, ipsa ipsum laudantium non
                    obcaecati quae saepe sapiente sint vero vitae.</Desc>
                <Info>
                    <Button>
                        START A PROJECT
                    </Button>
                    <Contact>
                        <Phone>Call Us 505-305-3030</Phone>
                        <ContactText>For any question or concern</ContactText>
                    </Contact>

                </Info>
            </Left>
            <Right>r</Right>

        </Container>
    );
};

export default Intro;
